import React from 'react';
import classes from './SettingTabs.module.css';
import './bootstrap.min.css';
function SettingTabs() {

  const [value,setValue] = React.useState(0)

  const actArray = []
  for(let i=0;i<8;i++){
    if (i===value){
      actArray.push(`${classes.btntabs} ${classes.activetabs}`)
    }
    else{
      actArray.push(`${classes.btntabs}`)
    }
  }

  return (
    <div className="container" >
         <ul style={{listStyle:'none'}}>
         <li className={`${classes.li}`} ><button type='button' className={actArray[0]} onClick={()=>{setValue(0)}}>Account</button></li>
         <li className={`${classes.li}`} ><button type='button' className={actArray[1]} onClick={()=>{setValue(1)}}>Profie</button></li>
         <li className={`${classes.li}`} ><button type='button' className={actArray[2]} onClick={()=>{setValue(2)}}>Safety & Privacy</button></li>
         <li className={`${classes.li}`} ><button type='button' className={actArray[3]} onClick={()=>{setValue(3)}}>Feed Settings</button></li>
         <li className={`${classes.li}`} ><button type='button' className={actArray[4]} onClick={()=>{setValue(4)}}>Notifications</button></li>
         <li className={`${classes.li}`} ><button type='button' className={actArray[5]} onClick={()=>{setValue(5)}}>Emails</button></li>
         <li className={`${classes.li}`} ><button type='button' className={actArray[6]} onClick={()=>{setValue(6)}}>Subscriptions</button></li>
         <li className={`${classes.li}`} ><button type='button' className={actArray[7]} onClick={()=>{setValue(7)}}>Chat & Messaging</button></li>
         </ul>
         <hr className='m-0' />
    </div>
  );
}

export default SettingTabs;
