import React from 'react';
import classes from './SettingTabs.module.css';
import './bootstrap.min.css';
function SettingTabs() {

  const [value,setValue] = React.useState(0)

  const actArray = []
  for(let i=0;i<4;i++){
    if (i===value){
      actArray.push('classes.btntabs')
    }
    else{
      actArray.push('btntabs')
    }
  }

  return (
    <div className="container-md">
      <h1>Create Tabs in ReactJS without using other modules</h1>
      <ul style={{listStyle:'none', marginLeft:'30%'}}>
        <li ><button type='button' className={'${classes.btntabs}'} onClick={()=>{setValue(0)}}>TAB One</button></li>
        <li ><button type='button' className={actArray[1]} onClick={()=>{setValue(1)}}>TAB Two</button></li>
        <li ><button type='button' className={actArray[2]} onClick={()=>{setValue(2)}}>TAB 3</button></li>
        <li ><button type='button' className={actArray[3]} onClick={()=>{setValue(3)}}>TAB Four</button></li>
      </ul>
      <br/><br/>
      <div>
        <h2>TAB NO: {value+1}</h2>
      </div>
    </div>
  );
}

export default SettingTabs;
