import React from 'react';
import './bootstrap.min.css';
import classes from './Account.module.css' 
import icon from './google-logo-9808.png'
import can from './red-trash-can-icon.png'

export default function FeedSettings() {
    var x, i, j, l, ll, selElmnt, a, b, c;
/* Look for any elements with the class "custom-select": */
x = document.getElementsByClassName("custom-select");
l = x.length;
for (i = 0; i < l; i++) {
  selElmnt = x[i].getElementsByTagName("select")[0];
  ll = selElmnt.length;
  /* For each element, create a new DIV that will act as the selected item: */
  a = document.createElement("DIV");
  a.setAttribute("class", "select-selected");
  a.innerHTML = selElmnt.options[selElmnt.selectedIndex].innerHTML;
  x[i].appendChild(a);
  /* For each element, create a new DIV that will contain the option list: */
  b = document.createElement("DIV");
  b.setAttribute("class", "select-items select-hide");
  for (j = 1; j < ll; j++) {
    /* For each option in the original select element,
    create a new DIV that will act as an option item: */
    c = document.createElement("DIV");
    c.innerHTML = selElmnt.options[j].innerHTML;
    c.addEventListener("click", function(e) {
        /* When an item is clicked, update the original select box,
        and the selected item: */
        var y, i, k, s, h, sl, yl;
        s = this.parentNode.parentNode.getElementsByTagName("select")[0];
        sl = s.length;
        h = this.parentNode.previousSibling;
        for (i = 0; i < sl; i++) {
          if (s.options[i].innerHTML == this.innerHTML) {
            s.selectedIndex = i;
            h.innerHTML = this.innerHTML;
            y = this.parentNode.getElementsByClassName("same-as-selected");
            yl = y.length;
            for (k = 0; k < yl; k++) {
              y[k].removeAttribute("class");
            }
            this.setAttribute("class", "same-as-selected");
            break;
          }
        }
        h.click();
    });
    b.appendChild(c);
  }
  x[i].appendChild(b);
  a.addEventListener("click", function(e) {
    /* When the select box is clicked, close any other select boxes,
    and open/close the current select box: */
    e.stopPropagation();
    closeAllSelect(this);
    this.nextSibling.classList.toggle("select-hide");
    this.classList.toggle("select-arrow-active");
  });
}

function closeAllSelect(elmnt) {
  /* A function that will close all select boxes in the document,
  except the current select box: */
  var x, y, i, xl, yl, arrNo = [];
  x = document.getElementsByClassName("select-items");
  y = document.getElementsByClassName("select-selected");
  xl = x.length;
  yl = y.length;
  for (i = 0; i < yl; i++) {
    if (elmnt == y[i]) {
      arrNo.push(i)
    } else {
      y[i].classList.remove("select-arrow-active");
    }
  }
  for (i = 0; i < xl; i++) {
    if (arrNo.indexOf(i)) {
      x[i].classList.add("select-hide");
    }
  }
}

/* If the user clicks anywhere outside the select box,
then close all select boxes: */
document.addEventListener("click", closeAllSelect);
    return(
        <div className={`${classes.tab}`}>
            <div className=''>
                <h2 className={`${classes.header}`}>Feed settings</h2>
                <h3 className={`${classes.Subheaders}`}>CONTENT PREFERENCES <hr className='mt-2'></hr></h3>
                <div className={`${classes.box}`}>
                    <div>
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Show mature (18+) content</h3>
                        <p className={`${classes.BoxText} ${classes.Subtext}`}>See NSFW (Not Safe for Work) mature and adult images, videos, written content, and other media in your Reddit feeds and search results.</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                        <label className={`${classes.switch}`}>
                            <input type="checkbox" />
                            <span className={`${classes.slider} ${classes.round}`}></span>
                        </label>
                    </div>
                </div>
                <div className={`${classes.box}`}>
                    <div>
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Blur mature images and media</h3>
                        <p className={`${classes.BoxText} ${classes.Subtext}`}>Blur previews and thumbnails for any images or videos tagged as NSFW (Not Safe for Work).</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                        <label className={`${classes.switch}`}>
                            <input type="checkbox" />
                            <span className={`${classes.slider} ${classes.round}`}></span>
                        </label>
                    </div>
                </div>
                <div className={`${classes.box}`}>
                    <div>
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Enable home feed recommendations</h3>
                        <p className={`${classes.BoxText} ${classes.Subtext}`}>Allow us to introduce recommended posts in your home feed.</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                        <label className={`${classes.switch}`}>
                            <input type="checkbox" />
                            <span className={`${classes.slider} ${classes.round}`}></span>
                        </label>
                    </div>
                </div>
                <div className={`${classes.box}`}>
                    <div>
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Autoplay media</h3>
                        <p className={`${classes.BoxText} ${classes.Subtext}`}>Play videos and gifs automatically when in the viewport.</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                        <label className={`${classes.switch}`}>
                            <input type="checkbox" />
                            <span className={`${classes.slider} ${classes.round}`}></span>
                        </label>
                    </div>
                </div>
                <div className={`${classes.box}`}>
                    <div>
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Reduce Animations</h3>
                        <p className={`${classes.BoxText} ${classes.Subtext}`}>Reduce animations on posts, comments, and feeds.</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                        <label className={`${classes.switch}`}>
                            <input type="checkbox" />
                            <span className={`${classes.slider} ${classes.round}`}></span>
                        </label>
                    </div>
                </div>
                <div className={`${classes.box}`}>
                    <div>
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Community themes</h3>
                        <p className={`${classes.BoxText} ${classes.Subtext}`}>Use custom themes for all communities. You can also turn this off on a per community basis.</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                        <label className={`${classes.switch}`}>
                            <input type="checkbox" />
                            <span className={`${classes.slider} ${classes.round}`}></span>
                        </label>
                    </div>
                </div>
                <div className={`${classes.box}`}>
                    <div>
                    <h3 className={`${classes.SettingTopics}`}>Gender</h3>
                    <p className={`${classes.Subtext} ${classes.font}`}>This information may be used to improve your recommendations and ads.</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                        {/* <div className={`${classes.dropdown}`}>
                            <button className={`${classes.dropbtn} `}><span className={`${classes.font}`}>SELECT</span></button>
                            <div className={`${classes.dropdownContent} ${classes.font}`}>
                                <a href="#">Male</a>
                                <a href="#">Woman</a>
                                <a href="#">Non-Binary</a>
                                <a href="#">I Refer To Myself As...</a>
                                <a href="#">I Prefer Not To Say</a>
                            </div>
                        </div> */}
                        <div class="custom-select">
                            <select>
                                <option value="0">Select car:</option>
                                <option value="1">Audi</option>
                                <option value="2">BMW</option>
                                <option value="3">Citroen</option>
                                <option value="4">Ford</option>
                                <option value="5">Honda</option>
                                <option value="6">Jaguar</option>
                                <option value="7">Land Rover</option>
                                <option value="8">Mercedes</option>
                                <option value="9">Mini</option>
                                <option value="10">Nissan</option>
                                <option value="11">Toyota</option>
                                <option value="12">Volvo</option>
                            </select>
                        </div>
                    </div>
                </div>
                <h3 className={`${classes.Subheaders}`}>CONNECTED ACCOUNTS <hr className='mt-2'></hr> </h3>
                <div >
                    <div className={`${classes.BoxText}`}>
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Connect to twitter</h3>
                        <p className={`${classes.Subtext}`}>Connect a Twitter account to enable the choice to tweet your new posts and display a link on your profile. We will never post to Twitter without your permission.</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                    <a href="#" className={`${classes.ConnectTwitter} ${classes.font}`}><span href="#" class="fa fa-twitter"></span>Connect with twitter</a>
                    </div><br></br>
                    <div className={`${classes.box} ${classes.disabled}`}>
                        <div className={`${classes.faded}`}>
                            <h3 className={`${classes.SettingTopics} ${classes.font}`}>Show link on profile</h3>
                            <p className={`${classes.Subtext} `} >You can show a link to your Twitter account on your profile</p>
                        </div>
                        <div className={`${classes.SettingToggles}`}>
                            <label className={`${classes.switch}`}>
                                <a type="checkbox" />
                                <span className={`${classes.slider} ${classes.round}`}></span>
                            </label>
                        </div>
                    </div>
                </div>
                <div className={`${classes.box}`}>
                    <div >
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Connect to Apple</h3>
                        <p className={`${classes.Subtext}`}>Connect account to log in to Reddit with Apple</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                    <a href="#" className={`${classes.ConnectApple} ${classes.font}`}><span href="#" class="fa fa-apple" id={`${classes.apple}`}></span>Connect With Apple</a>
                    </div>
                </div>
                <div className={`${classes.box}`}>
                    <div >
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Connect to Google</h3>
                        <p className={`${classes.Subtext}`}>Connect account to log in to Reddit with Google</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                    <a href="#" className={`${classes.ConnectGoogle} ${classes.font}`}><span href="#" className={`${classes.google}`}><img src={icon} className={`${classes.google}`}></img></span>Connect to Google</a>
                    </div>
                </div>
                <h3 className={`${classes.Subheaders}`}>BETA TESTS <hr className='mt-2'></hr> </h3>
                <div className={`${classes.box}`}>
                    <div>
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Opt into beta tests</h3>
                        <p className={`${classes.Subtext}`}>See the newest features from Reddit and join the r/beta community</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                        <label className={`${classes.switch}`}>
                            <input type="checkbox" />
                            <span className={`${classes.slider} ${classes.round}`}></span>
                        </label>
                    </div>
                </div>
                <div className={`${classes.box}`}>
                    <div>
                        <h3 className={`${classes.SettingTopics} ${classes.font}`}>Opt out of the redesign</h3>
                        <p className={`${classes.Subtext}`}>Revert back to old Reddit for the time being</p>
                    </div>
                    <div className={`${classes.SettingToggles}`}>
                        <label className={`${classes.switch}`}>
                            <input type="checkbox" />
                            <span className={`${classes.slider} ${classes.round}`}></span>
                        </label>
                    </div>
                </div>
                <h3 className={`${classes.Subheaders}`}>DELETE ACCOUNT<hr className='mt-2'></hr> </h3>
                <div className={`${classes.box}`}>
                    <div className={`${classes.SettingToggles}`}>
                        <a href='#' className={`${classes.DeleteAcc}`}><img src={can} className={`${classes.delete}`}></img>DELETE ACCOUNT</a>
                    </div>
                </div>
            </div>
        </div>
    )
}
